function drawTree(node) {
    if(!node.gyermekek.length) {
        return `<code>${node.ertek}</code>`
    }

    let gyermekekSablon = "";
    for (let gyermek of node.gyermekek) {
        gyermekekSablon += `<li>${drawTree(gyermek)}</li>`;
    }

    return `<code>${node.ertek}</code>
            <ul>
                ${gyermekekSablon}
            </ul>`
}

