  },
];

function buildTree(node, lapos) {
  const csakAGyermekElemek = lapos.filter((elem) => elem.szuloId === node.id);
  if (!csakAGyermekElemek.length) {
    return {
      id: node.id,
      szuloId: node.szuloId,
      ertek: node.ertek,
      gyermekek: [],
    };
  }
  let gyermekElemek = [];
  for (let gyermekElem of csakAGyermekElemek) {
    gyermekElemek.push(buildTree(gyermekElem, lapos));
  }
  return {
    id: node.id,
    szuloId: node.szuloId,
    ertek: node.ertek,
    gyermekek: gyermekElemek,
  };
}

